import React from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './index.css';

function Review(props) {
    const [currentRating, setCurrentRating] = useState(0);
    const starRatingRef = useRef(null);

    const handleMouseOver = (event) => {
        const target = event.target;
        if (target.classList.contains('fa-star')) {
        const hoveredIndex = parseInt(target.classList[1].split('-')[1], 10);
        setCurrentRating(hoveredIndex);
        starRatingRef.current.querySelectorAll('.fa-star').forEach((star) => star.classList.remove('checked'));
        for (let i = 1; i <= hoveredIndex; i++) {
            starRatingRef.current.querySelector(`.star-${i}`).classList.add('checked');
        }
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const reviewText = event.target.elements.reviewText.value;
        console.log(`Submitted rating: ${currentRating}`);
        console.log(`Review: ${reviewText}`);
        setCurrentRating(0);
    };
    return (
        <section className="review">
        <h1>Review:</h1>
        <p>Worked here before? Leave a Review!</p>
        <div className="star-rating">
            <span className="fa fa-star star-1"></span>
            <span className="fa fa-star star-2"></span>
            <span className="fa fa-star star-3"></span>
            <span className="fa fa-star star-4"></span>
            <span className="fa fa-star star-5"></span>
        </div>
        <textarea className="review-text" placeholder="Write your review here..."></textarea>
        <button type="button" className="submit-button">Submit Review</button>
        </section>
    );
}
export { Review };
